mapper.select:
SELECT nbno, nbtitle, nbwrittendate, nbhitscnt, nbisdel FROM ( SELECT rownum rnum,n1. *FROM( SELECT * FROM tb_noticeboard ORDER BY nbno DESC) n1
OR nbtitle LIKE '%' || #{searchText} || '%'
OR nbwrittendate LIKE '%' || #{searchText} || '%'
) WHERE rnum between #{paging.start} and #{paging.end}

			SELECT n.*, nf.*, ni.* FROM tb_noticeboard n, tb_noticefile nf, tb_noticeimg ni
    			  WHERE n.nbno = nf.nfnbno
    			  		AND nf.nfnbno = ni.ninbno
                        AND n.nbno = #{nbNo}
            ORDER BY n.nbno DESC;
