mapper.select:
select dbno, dbuno, dbtitle, dbcontent, dbwrittendate, dblikecnt, dbrecruitdate, dbrecruitarea, dbrecruitstatus, dbrecruitnumber, dbrecruitgender, dbrecruitcost, max(di.distoredimgname) as distoredimgnamestoredimg, agecheck, themecheck from tb_doboard d left outer join tb_doimg di on d.dbno = di.didbno left outer join (select dcdbno, LISTAGG(dcvalue, ',') WITHIN GROUP (order by dcvalue) as agecheck from tb_docheck where dccate='age' group by dcdbno) agecheck on d.dbno = agecheck.dcdbno left outer join (select dcdbno, LISTAGG(dcvalue, ',') WITHIN GROUP (order by dcvalue) as themecheck from tb_docheck where dccate='theme' group by dcdbno) themecheck on d.dbno = themecheck.dcdbno where d.dbisdel = 0 and d.dbtitle || d.dbcontent LIKE '%' || #{searchText} || '%'
and ( dbrecruitarea IN
#{item}
)
and ( dbrecruitstatus IN
#{item}
)
and ( dbrecruitgender IN
#{item}
)
and (
agecheck like '%' || #{item} || '%'
)
and (
themecheck like '%' || #{item} || '%'
)
group by dbno, dbuno, dbtitle, dbcontent, dbwrittendate, dblikecnt, dbrecruitdate, dbrecruitarea, dbrecruitstatus, dbrecruitnumber, dbrecruitgender, dbrecruitcost, agecheck, themecheck order by dbwrittendate desc
